mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY RV_IDX DESC) RNUM, R.RV_IDX, R.RV_M_IDX, R.RV_MO_IDX, R.RV_CONTENT, R.RV_REGDATE, R.RV_TITLE, R.RV_DEL_YN, R.RV_STAR, F.F_IDX, F.F_TABLE, F.F_ARTICLE, F.F_OGNAME, F.F_SVNAME, F.F_SIZE, F.F_MAIN_YN, F.F_DEL_YN, F.F_REG_DATE, M.MO_IDX, M.M_IDX, M.MO_TITLE, M.MO_DETAIL, M.MO_CATEGORY, M.MO_DETAILCATEGORY, M.MO_REGION, M.MO_DETAILREGION, M.MO_PERMIT, M.MO_GENDER, M.MO_MINAGE, M.MO_MAXAGE, M.MO_MAXPEOPLE, M.MO_COST, M.MO_DEADLINE, M.MO_DEADTIME, M.MO_REGDATE, M.MO_CLOSE_YN, M.MO_DEL_YN FROM REVIEW R JOIN MOIM M ON R.RV_MO_IDX = M.MO_IDX JOIN FILE_T F ON R.RV_IDX = F.F_ARTICLE WHERE F_TABLE = 'F' AND F_MAIN_YN = 'N' AND F_DEL_YN = 'N' AND RV_DEL_YN = 'N' AND M.MO_CATEGORY = #{mo_category}
AND M.MO_REGION = #{mo_region}
ORDER BY F.F_REG_DATE DESC
